mapper.select:
select
id, url, uri, ip, execute_time, request_time, request, execute_result
from service_inteface_info


        and id = #{id}


        and url = #{url}


        and uri = #{uri}


        and ip = #{ip}


        and execute_time = #{executeTime}


        and request_time = #{requestTime}


        and request = #{request}


        and execute_result = #{executeResult}
